mapper.insert:
insert into sys_user
id, user_id, user_name, password, id_number
#{id, jdbcType=VARCHAR}, #{userId, jdbcType=VARCHAR}, #{userName, jdbcType=VARCHAR}, #{password, jdbcType=VARCHAR}, #{idNumber, jdbcType=VARCHAR}

mapper.select:
select id,user_name as userName from sys_user
where user_id = #{userId, jdbcType=VARCHAR}
and password = #{password, jdbcType=VARCHAR}
